glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform Properties {
  float round;
  float feather;
};
void main () {
  vec4 color = v_color;
  float dist = distance(v_uv0, vec2(0.5, 0.5));
  float smoothDist = 1.0 - smoothstep(0.49, 0.5, dist);
  color *= texture(texture, v_uv0);
  color.a *= v_color.a * smoothDist;
  gl_FragColor = color;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
attribute vec3 a_position;
attribute vec2 a_uv0;
attribute vec4 a_color;
varying vec2 v_uv0;
varying vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl1.frag:
precision highp float;
varying vec2 v_uv0;
varying vec4 v_color;
uniform sampler2D texture;
void main () {
  vec4 color = v_color;
  float dist = distance(v_uv0, vec2(0.5, 0.5));
  float smoothDist = 1.0 - smoothstep(0.49, 0.5, dist);
  color *= texture2D(texture, v_uv0);
  color.a *= v_color.a * smoothDist;
  gl_FragColor = color;
}
